import './Task.css'

const Task = ({task, handleTaskClick}) => {
    
    return (  
        <div className="task_container" style={task.completed ? {borderLeft: "6px solid chartreuse"}:{}} >
        
        <div className='task-title' onClick={()=>handleTaskClick()}>
            {task.title}
        </div>

        </div>
    );
}
 
export default Task;
