import React,{useState} from 'react';
import "./App.css"
import AddTasks from './components/AddTasks';
import Tasks from './components/Tasks';


function App() {
  const [tasks, setTasks]= useState([
    {
    id:"1",
    title:"Ler Livros",
    completed: false,
  },
  {
    id:"2",
    title:"Estudos recentes",
    completed: false,
  },
])

const handleTaksAddition = (taskTitle)=>{
const newTask = [... tasks, {
  title: taskTitle,
  id:Math.random(10),
  completed:false
}]
setTasks(newTask)
}



  return (
<div className='container_app'>
    <AddTasks  handleTaksAddition={handleTaksAddition} />
    <Tasks tasks={tasks} />
</div>
  );
}

export default App;
